import React, { useEffect, useState } from "react";
import ProductTitle from "./ProductTitle";
import { useParams}  from "react-router-dom";
import { getProductById } from "../services/Api";
import BookingForm from "./BookingForm";
import BookingCalendar from "./BookingCalendar";
import BookingSummary from "./BookingSummary";
import '../Styles/Booking.css'
import BookingTimer from "./BookingTimer";

const Booking = () => {
    const { id } = useParams();
    const [product, setProduct] = useState({});

    const [dateRange, setDateRange] = useState([null, null]);
    const [startDate, endDate] = dateRange;
    useEffect(() => {
    if (id) {
        getProductById(id).then((data) => {
        setProduct(data.data);
        });
    }
    }, []);

    return (
        <div className="booking-page">
            <ProductTitle {...product}/>
            <div className="booking-info">
            <div className="booking-info-left">
                <h2>Complet√° tus datos</h2>
                <BookingForm />
                <BookingCalendar dateRange={dateRange} setDateRange={setDateRange} startDate={startDate} endDate={endDate}/>
                <BookingTimer/>
            </div>
            <div className="booking-info-right">
                <BookingSummary {...product} startDate={startDate} endDate={endDate} />
            </div>
            </div>
        </div>
    )
}

export default Booking
